package Loops;

public class Switch {
    public static void main(String[] args) {
        // TODO Auto-generated method stub

        switch(1)
        {
            case 0:
                System.out.println(0);
            case 1:
                System.out.println(1);
                break;
            case 2:
                System.out.println(2);
            default:
                System.out.println("def");
        }
    }
}

